from dataclasses import dataclass
from typing import List

@dataclass
class FitnessGoal:
    """Represents a concrete fitness goal with a target and current progress."""
    name: str
    target_value: float
    current_value: float
    unit: str

    @property
    def progress_percentage(self) -> float:
        if self.target_value == 0:
            return 0.0
        return min((self.current_value / self.target_value) * 100, 100.0)

# A collection of 5 concrete fitness goals to bootstrap the Goal Evolver
fitness_goals: List[FitnessGoal] = [
    FitnessGoal(name="Run 5km", target_value=5.0, current_value=2.5, unit="km"),
    FitnessGoal(name="Lose 5kg", target_value=5.0, current_value=1.2, unit="kg"),
    FitnessGoal(name="Bench Press 80kg", target_value=80.0, current_value=60.0, unit="kg"),
    FitnessGoal(name="30-day Plank Challenge", target_value=300.0, current_value=90.0, unit="seconds"),
    FitnessGoal(name="Drink 3L Water Daily", target_value=3.0, current_value=1.5, unit="liters"),
]

# Example usage to demonstrate the data points
if __name__ == "__main__":
    for goal in fitness_goals:
        print(f"{goal.name}: {goal.current_value}/{goal.target_value} {goal.unit} ({goal.progress_percentage:.2f}%)")
